CREATE OR REPLACE FUNCTION consistir_horario_corte_gn ( dt_servico_p timestamp, dt_liberado_p timestamp, cd_setor_atend_p bigint, nr_seq_servico_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(5);
dt_inicio_corte_w	timestamp;
dt_fim_corte_w		timestamp;
dt_inicio_w		varchar(10);
dt_fim_w		varchar(10);


BEGIN
ds_retorno_w := '';

if (dt_liberado_p IS NOT NULL AND dt_liberado_p::text <> '') then

	select	max(ds_horario_corte_inic),
		max(ds_horario_corte_fim),
		max(to_date(to_char(dt_liberado_p,'dd/mm/yyyy') || ' ' || ds_horario_corte_inic, 'dd/mm/yyyy hh24:mi:ss')),
		max(to_date(to_char(dt_liberado_p,'dd/mm/yyyy') || ' ' || ds_horario_corte_fim, 'dd/mm/yyyy hh24:mi:ss'))
	into STRICT	dt_inicio_w,
		dt_fim_w,
		dt_inicio_corte_w,
		dt_fim_corte_w
	from 	NUT_SERVICO_HORARIO
	where	nr_seq_servico = nr_seq_servico_p
	and	coalesce(cd_setor_atendimento,cd_setor_atend_p) = coalesce(cd_setor_atend_p,0);


	if (dt_inicio_w IS NOT NULL AND dt_inicio_w::text <> '') and (dt_fim_w IS NOT NULL AND dt_fim_w::text <> '') and (trunc(dt_liberado_p) >= trunc(dt_servico_p)) then
		if ((dt_liberado_p > dt_fim_corte_w) or (dt_liberado_p < dt_inicio_corte_w)) then
			ds_retorno_w := 'S';
		end if;
	elsif (dt_inicio_w IS NOT NULL AND dt_inicio_w::text <> '') and (trunc(dt_liberado_p) >= trunc(dt_servico_p)) then
		if (dt_liberado_p < dt_inicio_corte_w) then
			ds_retorno_w := 'S';
		end if;
	elsif (dt_fim_w IS NOT NULL AND dt_fim_w::text <> '') and (trunc(dt_liberado_p) >= trunc(dt_servico_p)) then
		if (dt_liberado_p > dt_fim_corte_w) then
			ds_retorno_w := 'S';
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
