CREATE OR REPLACE FUNCTION consistir_interv_item_composto (nr_prescricao_p bigint, nr_seq_material_p bigint, nr_agrupamento_p bigint, cd_intervalo_p text) RETURNS varchar AS $body$
DECLARE


ie_intervalo_valido_w	varchar(1);


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') and (cd_intervalo_p IS NOT NULL AND cd_intervalo_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ie_intervalo_valido_w
	from	prescr_material
	where	nr_prescricao	= nr_prescricao_p
	and	nr_sequencia	<> nr_seq_material_p
	and	nr_agrupamento	= nr_agrupamento_p
	and	cd_intervalo	<> cd_intervalo_p
	and	ie_agrupador	= 1;

end if;

return ie_intervalo_valido_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
