SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION consistir_inter_atrib_regra ( nm_tabela_p text, nm_atributo_p text, vl_atributo_p bigint) RETURNS varchar AS $body$
DECLARE


vl_minimo_permitido_w	double precision	:= Null;
vl_maximo_permitido_w	double precision	:= Null;
ds_label_w		varchar(60);
AReturn_w		varchar(500);


BEGIN

begin
select	vl_minimo_permitido,
	vl_maximo_permitido
into STRICT	vl_minimo_permitido_w,
	vl_maximo_permitido_w
from	tabela_atrib_regra
where	nm_tabela	= nm_tabela_p
and	nm_atributo	= nm_atributo_p;
exception
	when others then
	vl_minimo_permitido_w	:= Null;
	vl_maximo_permitido_w	:= Null;
end;

begin
select	coalesce(ds_label, nm_atributo)
into STRICT	ds_label_w
from	tabela_atributo
where	nm_tabela	= nm_tabela_p
and	nm_atributo	= nm_atributo_p;
exception
	when others then
	ds_label_w		:= '';
end;

if (vl_minimo_permitido_w IS NOT NULL AND vl_minimo_permitido_w::text <> '') and (vl_maximo_permitido_w IS NOT NULL AND vl_maximo_permitido_w::text <> '') and
	((vl_atributo_p	< vl_minimo_permitido_w) or (vl_atributo_p > vl_maximo_permitido_w)) then
	AReturn_w := obter_desc_expressao(786869); /*O valor informado para #@DS_LABEL#@ est√° fora da faixa de valores permitidos: #@VL_MINIMO#@ e #@VL_MAXIMO#@.*/
	AReturn_w := replace(AReturn_w, '#@DS_LABEL#@', ds_label_w);
	AReturn_w := replace(AReturn_w, '#@VL_MINIMO#@', vl_minimo_permitido_w);
	AReturn_w := replace(AReturn_w, '#@VL_MAXIMO#@', vl_maximo_permitido_w);
else
	AReturn_w	:= '';
end	if;

Return AReturn_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
