SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION consistir_justif_perfil (cd_justificativa_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


retorno 		varchar(1) := 'N';
qt_regra_w		bigint;



BEGIN


select	count(1)
into STRICT	qt_regra_w
from	regra_justif_tempo a
where	a.ie_situacao = 'A'
and	a.cd_justificativa = cd_justificativa_p
and	((cd_perfil_p in (	SELECT	cd_perfil
				from	regra_justif_tempo_item b
				where	b.nr_seq_justif = a.nr_sequencia))
	or (0 = (select	count(cd_perfil)
		from	regra_justif_tempo_item b
		where	b.nr_seq_justif = a.nr_sequencia)));

if (qt_regra_w > 0)then
retorno := 'S';
end if;

return	retorno;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
