CREATE OR REPLACE FUNCTION consistir_lista_espera_ageserv ( cd_pessoa_fisica_p text, dt_desejada_p timestamp) RETURNS varchar AS $body$
DECLARE

		 
ds_retorno_w	varchar(1)	:= 'N';


BEGIN 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (dt_desejada_p IS NOT NULL AND dt_desejada_p::text <> '') then 
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
	into STRICT	ds_retorno_w 
	from Agenda_Lista_Espera 
	where cd_pessoa_fisica = cd_pessoa_fisica_p 
	and dt_desejada = dt_desejada_p 
	and IE_STATUS_ESPERA = 'A';
end if;
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
