SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION consistir_medic_dcb ( cd_material_old_p bigint, cd_material_new_p bigint) RETURNS varchar AS $body$
DECLARE


qt_medic_w		bigint;
ie_retorno_w		varchar(1);


BEGIN
begin
select	count(*)
into STRICT	qt_medic_w
from 	(	SELECT	a.*
		from	material a,
			medic_ficha_tecnica b
		where	a.nr_seq_ficha_tecnica	= b.nr_sequencia
		and	b.nr_seq_dcb		= (	select	d.nr_seq_dcb
							from	material c,
								medic_ficha_tecnica d
							where	c.nr_seq_ficha_tecnica	= d.nr_sequencia
							and	c.cd_material 		= cd_material_new_p)) alias2
where	cd_material = cd_material_old_p;
exception
	when others then
		qt_medic_w	:= 0;
end;


if (qt_medic_w > 0) then
	ie_retorno_w := 'S';
else
	ie_retorno_w := 'N';
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
