CREATE OR REPLACE FUNCTION consistir_medic_prescritor ( cd_material_p bigint, cd_pessoa_fisica_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_classe_material_w		integer;
cd_subgrupo_material_w		smallint;
cd_grupo_material_w		smallint;
qt_reg_regra_w			smallint	:= 0;
ie_medico_habilitado_w		varchar(1)	:= 'S';


BEGIN

select	cd_classe_material,
	cd_subgrupo_material,
	cd_grupo_material
into STRICT	cd_classe_material_w,
	cd_subgrupo_material_w,
	cd_grupo_material_w
from	estrutura_material_v
where	cd_material	= cd_material_p;

select	count(*)
into STRICT	qt_reg_regra_w
from	regra_medic_prescritor
where	cd_estabelecimento	= cd_estabelecimento_p
and	coalesce(cd_classe_material,cd_classe_material_w)	= cd_classe_material_w
and	coalesce(cd_subgrupo_material,cd_subgrupo_material_w)= cd_subgrupo_material_w
and	coalesce(cd_grupo_material,cd_grupo_material_w)	= cd_grupo_material_w
and	coalesce(cd_material,cd_material_p)	= cd_material_p;

if (qt_reg_regra_w	> 0) then
	select	count(*)
	into STRICT	qt_reg_regra_w
	from	regra_medic_prescritor
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	coalesce(cd_classe_material,cd_classe_material_w)	= cd_classe_material_w
	and	coalesce(cd_subgrupo_material,cd_subgrupo_material_w)= cd_subgrupo_material_w
	and	coalesce(cd_grupo_material,cd_grupo_material_w)	= cd_grupo_material_w
	and	coalesce(cd_material,cd_material_p)	= cd_material_p;

	if (qt_reg_regra_w	= 0) then
		ie_medico_habilitado_w	:= 'N';
	end if;
end if;

return ie_medico_habilitado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
