CREATE OR REPLACE FUNCTION consistir_mesmo_medicamento ( cd_pessoa_fisica_p bigint, cd_material_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

 
			ds_retorno_w		varchar(100);
ie_existe_material_w	bigint;			
 

BEGIN 
 
select	count(*) 
into STRICT	ie_existe_material_w 
from 	medic_uso_continuo 
where	cd_pessoa_fisica = cd_pessoa_fisica_p 
and	cd_material = cd_material_p 
and	nr_sequencia <> nr_sequencia_p 
and	coalesce(dt_suspensao::text, '') = '';
 
if (ie_existe_material_w > 0 ) then 
	-- 'Medicamento j√° cadastrado para o paciente ' || OBTER_NOME_PESSOA_FISICA(cd_pessoa_fisica_p,null); 
	ds_retorno_w := Wheb_mensagem_pck.get_texto(306539, 'NM_PESSOA_FISICA='||OBTER_NOME_PESSOA_FISICA(cd_pessoa_fisica_p,null));
else 
	ds_retorno_w := '';
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
