CREATE OR REPLACE FUNCTION consistir_orgao_emissor (ds_orgao_emissor_ci_p text) RETURNS varchar AS $body$
DECLARE


ie_valido_w	varchar(1);


BEGIN
if (ds_orgao_emissor_ci_p IS NOT NULL AND ds_orgao_emissor_ci_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_valido_w
	from	orgao_emissor
	where	upper(cd_orgao_emissor) = upper(ds_orgao_emissor_ci_p);

end if;

return ie_valido_w;

end;
$body$
