CREATE OR REPLACE FUNCTION consistir_pr_rateio_proc ( nr_seq_proc_interno_p bigint, pr_rateio_p bigint) RETURNS varchar AS $body$
DECLARE


pr_rateio_w	double precision;
ie_retorno_w	varchar(1);


BEGIN

select	coalesce(sum(pr_rateio),0) + pr_rateio_p
into STRICT	pr_rateio_w
from	ctb_crit_rateio_proc
where	nr_seq_proc_interno = nr_seq_proc_interno_p
and	clock_timestamp() between dt_inicio_vigencia and coalesce(dt_fim_vigencia,clock_timestamp());

if (pr_rateio_w > 100) then
	ie_retorno_w := 'F';
else
	ie_retorno_w := 'V';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
