SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION consistir_recomendacao_mat ( nr_prescricao_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_consistir_w		varchar(1) := 'N';
cd_tipo_recomendacao_w	bigint;
count_w			bigint;


BEGIN

select	max(cd_tipo_recomendacao)
into STRICT	cd_tipo_recomendacao_w
from	material
where 	cd_material = cd_material_p;

if (cd_tipo_recomendacao_w > 0) then

	select	count(*)
	into STRICT	count_w
	from	prescr_recomendacao
	where 	nr_prescricao	= nr_prescricao_p
	and	cd_recomendacao = cd_tipo_recomendacao_w;

	if (count_w > 0) then
		ie_consistir_w := 'N';
	else
		ie_consistir_w := 'S';
	end if;
end if;


return	ie_consistir_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
