SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION consistir_regra_alta_cpf (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

			 
qt_regra_w		bigint;
ds_retorno_w		varchar(255);
cd_pessoa_fisica_w	varchar(10);
nr_cpf_w		varchar(11);
qt_idade_pessoa_w	integer;
ie_exige_cpf_w		varchar(1);


BEGIN 
 
ds_retorno_w := '';
select	count(*) 
into STRICT	qt_regra_w 
from	regra_consiste_alta 
where	ie_situacao	= 'A' 
and	ie_exige_cpf in ('S','M');
 
if (qt_regra_w > 0) and (nr_atendimento_p <> 0) then 
	select coalesce(max(cd_pessoa_fisica),0) 
	into STRICT  cd_pessoa_fisica_w 
	from  atendimento_paciente 
	where  nr_atendimento = nr_atendimento_p;
 
	if (cd_pessoa_fisica_w <> 0) then 
		select	max(nr_cpf) 
		into STRICT	nr_cpf_w 
		from	pessoa_fisica 
		where	cd_pessoa_fisica	= cd_pessoa_fisica_w;
 
		if (coalesce(nr_cpf_w::text, '') = '') then 
			select	max(nr_cpf) 
			into STRICT	nr_cpf_w 
			from	compl_pessoa_fisica 
			where	cd_pessoa_fisica	= cd_pessoa_fisica_w 
			and	ie_tipo_complemento	= 3;	
 
		end if;
		 
		 
		select	max(ie_exige_cpf) 
		into STRICT	ie_exige_cpf_w 
		from	regra_consiste_alta 
		where	ie_situacao	= 'A' 
		and	ie_exige_cpf in ('S','M');
		 
		qt_idade_pessoa_w := Obter_Idade_PF(cd_pessoa_fisica_w,clock_timestamp(),'A');
		 
		if (coalesce(nr_cpf_w::text, '') = '') then 
			if (ie_exige_cpf_w = 'S') then 
				ds_retorno_w:= substr(obter_texto_tasy(318504,null),1,255);
				--'É obrigatório a informação do CPF do paciente conforme regra cadastrada.'; 
			elsif (ie_exige_cpf_w = 'M') and (qt_idade_pessoa_w >= 18) then 
				ds_retorno_w:= substr(obter_texto_tasy(318504,null),1,255);
				--'É obrigatório a informação do CPF do paciente conforme regra cadastrada.'; 
			end if;
		end if;
	end if;	
end if;
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
