CREATE OR REPLACE FUNCTION consistir_regra_altura_peso (nr_seq_exame_p bigint, cd_material_exame_p text) RETURNS varchar AS $body$
DECLARE

ie_peso_altura_rep_w	varchar(2000);

BEGIN
	if (nr_seq_exame_p IS NOT NULL AND nr_seq_exame_p::text <> '') and (cd_material_exame_p IS NOT NULL AND cd_material_exame_p::text <> '') then
		ie_peso_altura_rep_w	:= obter_se_mat_lab_peso_alt_rep(cd_material_exame_p);

		if (coalesce( ie_peso_altura_rep_w ,'N') = 'N') then
			ie_peso_altura_rep_w	:= obter_se_exame_peso_alt_rep(nr_seq_exame_p,cd_material_exame_p);
		end if;

		if ( ie_peso_altura_rep_w = 'S') then
			ie_peso_altura_rep_w :=  substr(obter_texto_tasy(239846, wheb_usuario_pck.get_nr_seq_idioma),1,255);
		end if;
	end if;

return	ie_peso_altura_rep_w;

end;
