SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION consistir_regra_assist (nr_seq_regra_p integer, ie_assistencial_p text default null, ie_acao_opcional_p text default null) RETURNS varchar AS $body$
DECLARE


    ie_tem_consistencia_w varchar(3) := null;
    nr_seq_regra_w      integer;
    ds_mensagem_w       varchar(2000);

BEGIN

    if (ie_assistencial_p = 'S') then

        select max(coalesce('S', null))
        into STRICT   ie_tem_consistencia_w
        from   gqa_acao a
        where  a.nr_seq_pend_regra = nr_seq_regra_p
        and    coalesce(a.ie_situacao, 'A') = 'A';

        if (ie_tem_consistencia_w IS NOT NULL AND ie_tem_consistencia_w::text <> '') then

            ds_mensagem_w := wheb_mensagem_pck.get_texto(446474); --Regras utilizadas para protocolos assistenciais não podem ter ações ativas
        end if;

     end if;

    if (ie_acao_opcional_p = 'S') then

        select max(coalesce('S', null))
        into STRICT   ie_tem_consistencia_w
        from   gqa_acao a
        where  a.nr_seq_pend_regra = nr_seq_regra_p
        and    coalesce(a.ie_situacao, 'A') = 'A'
        and    coalesce(a.ie_executar, 'N') = 'S';

        if (ie_tem_consistencia_w IS NOT NULL AND ie_tem_consistencia_w::text <> '') then

            ds_mensagem_w := wheb_mensagem_pck.get_texto(472009);     --Regras opcionais não podem ter ações com a opção "Gerar ação já liberada conforme regra" marcada
        end if;

     end if;


    return  ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
