CREATE OR REPLACE FUNCTION consistir_regra_gerar_hist_gv (cd_perfil_p bigint, ie_status_p text) RETURNS varchar AS $body$
DECLARE


ie_gerar_hist_gv_w	varchar(1);


BEGIN

if (coalesce(cd_perfil_p,0) > 0) then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_gerar_hist_gv_w
	from	regra_gerar_hist_gv
	where	coalesce(cd_perfil, cd_perfil_p) = cd_perfil_p
	and		coalesce(ie_status, ie_status_p) = ie_status_p;

end if;

return	ie_gerar_hist_gv_w;

end;
