SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION consistir_regra_mapa_refeicao (dt_data_p timestamp, cd_refeicao_p text) RETURNS varchar AS $body$
DECLARE


var_retorno_w	varchar(10);
dt_regra_w		timestamp;
dt_atual_w		timestamp;
ds_horario_w	varchar(5) := '';

BEGIN

select ds_horario
into STRICT ds_horario_w
from mapa_regra_horario
where cd_refeicao = cd_refeicao_p;

var_retorno_w := 'S';
if (ds_horario_w IS NOT NULL AND ds_horario_w::text <> '') then

	SELECT TO_DATE(TO_CHAR(clock_timestamp(),'dd/mm/yyyy'),'dd/mm/yyyy'),
		   TO_DATE(to_char(dt_data_p,'dd/mm/yyyy'),'dd/mm/yyyy')
	into STRICT dt_regra_w,
	     dt_atual_w
	;

	if (dt_atual_w >= dt_regra_w) then

		if (dt_atual_w = dt_regra_w) then
			SELECT TO_DATE(TO_CHAR(clock_timestamp(),'dd/mm/yyyy')||' '||ds_horario_w,'dd/mm/yyyy hh24:mi'),
				   TO_DATE(to_char(dt_data_p,'dd/mm/yyyy')||' '||TO_CHAR(clock_timestamp(),'hh24:mi'),'dd/mm/yyyy hh24:mi')
			into STRICT dt_regra_w,
			     dt_atual_w
			;

			if (dt_regra_w > dt_atual_w) then
				var_retorno_w := 'N';
			end if;
		else
			var_retorno_w := 'N';
		end if;
	end if;
else
	var_retorno_w := 'N';
end if;

return	var_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
