CREATE OR REPLACE FUNCTION consistir_setor_agenda_prescr (nr_seq_agenda_p text, nr_prescricao_p text, nr_seq_prescr_p text) RETURNS varchar AS $body$
DECLARE

			 
ds_retorno_w		varchar(1);
cd_setor_exclusivo_w	integer;
cd_setor_exec_w		integer;
cd_agenda_w		bigint;
cd_tipo_agenda_w	bigint;


BEGIN 
 
select	max(cd_agenda) 
into STRICT	cd_agenda_w 
from	agenda_paciente 
where	nr_sequencia = nr_seq_agenda_p;
 
select	max(coalesce(cd_setor_exclusivo,0)), 
	max(cd_tipo_agenda) 
into STRICT	cd_setor_exclusivo_w, 
	cd_tipo_agenda_w 
from	agenda 
where	cd_agenda = cd_agenda_w;
 
select	max(coalesce(b.cd_setor_atendimento,0)) 
into STRICT	cd_setor_exec_w 
from	prescr_medica a, 
	prescr_procedimento b 
where	a.nr_prescricao = b.nr_prescricao 
and	a.nr_prescricao = nr_prescricao_p 
and	b.nr_sequencia = nr_seq_prescr_p;
 
if (cd_tipo_agenda_w = 2) and (cd_setor_exclusivo_w > 0) and (cd_setor_exclusivo_w = cd_setor_exec_w) then 
	ds_retorno_w	:=	'S';
else 
	ds_retorno_w	:=	'N';
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
