CREATE OR REPLACE FUNCTION consistir_se_mat_estrut_vig ( nr_seq_estrutura_p bigint, cd_material_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);
qt_reg_w	bigint;


BEGIN

select 	count(*)
into STRICT	qt_reg_w
from	mat_estrutura a,
	mat_estrutura_cadastro b,
	mat_estrutura_cad_vig c
where	a.nr_sequencia	= b.nr_seq_estrutura
and	a.nr_sequencia	= nr_seq_estrutura_p
and	b.cd_material	= cd_material_p
and 	c.nr_seq_mat_estrutura = b.nr_sequencia;


if (qt_reg_w > 0) then

	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	mat_estrutura a,
		mat_estrutura_cadastro b,
		mat_estrutura_cad_vig c
	where	a.nr_sequencia	= b.nr_seq_estrutura
	and	a.nr_sequencia	= nr_seq_estrutura_p
	and	b.cd_material	= cd_material_p
	and 	c.nr_seq_mat_estrutura = b.nr_sequencia
	and 	dt_referencia_p between c.dt_inicio_vigencia and coalesce(dt_final_vigencia, dt_referencia_p);

else

	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	mat_estrutura a,
		mat_estrutura_cadastro b
	where	a.nr_sequencia	= b.nr_seq_estrutura
	and	a.nr_sequencia	= nr_seq_estrutura_p
	and	b.cd_material	= cd_material_p;

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
