CREATE OR REPLACE FUNCTION consistir_vigencia_credenc (dt_inicial_p timestamp, dt_final_p timestamp, dt_atual_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_vigente_w	varchar(1);


BEGIN
if (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') and (dt_final_p IS NOT NULL AND dt_final_p::text <> '') and (dt_atual_p >= dt_inicial_p) and (dt_atual_p <= dt_final_p) then

	ie_vigente_w	:= 'S';

elsif (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') and (coalesce(dt_final_p::text, '') = '') and (dt_atual_p >= dt_inicial_p) then

	ie_vigente_w	:= 'S';

else

	ie_vigente_w	:= 'N';

end if;

return ie_vigente_w;

end;
