CREATE OR REPLACE FUNCTION consisty_rule_prio_insurance ( nr_atendimento_p atend_categoria_convenio.nr_atendimento%type, cd_convenio_p atend_categoria_convenio.cd_convenio%type, nr_prioridade_p atend_categoria_convenio.nr_prioridade%type, nr_seq_interno_p atend_categoria_convenio.nr_seq_interno%type) RETURNS bigint AS $body$
DECLARE


cd_convenio_w		atend_categoria_convenio.cd_convenio%type;
nr_atendimento_w 	atend_categoria_convenio.nr_atendimento%type;
ie_show_consistency_w	varchar(1);

c_Insurances CURSOR FOR
SELECT	acc.cd_convenio cd_convenio,
	acc.nr_prioridade nr_prioridade,
	acc.nr_seq_interno nr_seq_interno,
	acc.nr_atendimento nr_atendimento,
	ap.dt_cancelamento
from	atend_categoria_convenio acc,
        atendimento_paciente ap
where	acc.nr_atendimento	= ap.nr_atendimento
and	acc.nr_seq_interno	 <> nr_seq_interno_p
and	coalesce(ap.dt_cancelamento::text, '') = ''
and	ap.nr_seq_episodio 	= (SELECT x.nr_seq_episodio from atendimento_paciente x where x.nr_atendimento = nr_atendimento_p);

BEGIN

ie_show_consistency_w := 'N';
nr_atendimento_w := 0;

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '')then

	for c_Insurances_w in c_Insurances loop
	
		if (coalesce(c_Insurances_w.dt_cancelamento::text, '') = '') then
			if (c_Insurances_w.cd_convenio = cd_convenio_p and c_Insurances_w.nr_prioridade <> nr_prioridade_p) then
				CALL replicar_convenios_atendimento(nr_seq_interno_p, nr_atendimento_p, c_Insurances_w.nr_atendimento);
			elsif (c_Insurances_w.nr_prioridade = nr_prioridade_p and c_Insurances_w.cd_convenio <> cd_convenio_p) then
				ie_show_consistency_w := 'S';
				nr_atendimento_w := c_Insurances_w.nr_atendimento;
			end if;
		end if;

	end loop;

end if;

return nr_atendimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
