CREATE OR REPLACE FUNCTION consis_estab_usua_mont_consig (ie_opcao_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE




ie_restr_estab_montagem_w	varchar(1);
ie_restr_estab_consig_w		varchar(1);
ds_retorno_w			varchar(1) := 'S';


BEGIN

ie_restr_estab_montagem_w := obter_param_usuario(871, 755, wheb_usuario_pck.get_cd_perfil, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_restr_estab_montagem_w);
ie_restr_estab_consig_w := obter_param_usuario(871, 756, wheb_usuario_pck.get_cd_perfil, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_restr_estab_consig_w);



if (ie_opcao_p = 'M' AND ie_restr_estab_montagem_w = 'S') then

	SELECT 	coalesce(MAX('S'),'N')
	into STRICT	ds_retorno_w
	from    usuario_estabelecimento_v a
	where   a.cd_estabelecimento = cd_estabelecimento_p
	and	a.nm_usuario_param = wheb_usuario_pck.get_nm_usuario;

elsif (ie_opcao_p = 'PC' AND ie_restr_estab_consig_w = 'S') then

       SELECT 	coalesce(MAX('S'),'N')
       into STRICT	ds_retorno_w
       from    usuario_estabelecimento_v a
       where   a.cd_estabelecimento = cd_estabelecimento_p
       and	a.nm_usuario_param = wheb_usuario_pck.get_nm_usuario;

end if;

return	ds_retorno_w;

end;
$body$
