CREATE OR REPLACE FUNCTION consultar_funcao_schematic ( nr_sequencia_p funcao_schematic.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w    varchar(1) := 'N';
qt_requistro_w 	integer(2);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select  count(1)
	into STRICT 	qt_requistro_w
	from	funcao_schematic
	where   nr_sequencia 	= nr_sequencia_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

	if ( qt_requistro_w = 1 ) then
		ds_retorno_w := 'S';
	end if;
end if;

return    ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
