SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION consultar_niveis_ccg (nr_seq_horario_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_glicemia_w	bigint := 0;
nr_prescricao_w		bigint;
nr_seq_procedimento_w	bigint;


BEGIN

if (nr_seq_horario_p > 0) then
	select  a.nr_prescricao,
		coalesce(a.nr_seq_procedimento,0)
	into STRICT 	nr_prescricao_w,
		nr_seq_procedimento_w
	from    prescr_proc_hor a
	where   a.nr_sequencia  = nr_seq_horario_p;

	select 	coalesce(max(a.nr_sequencia),0)
	into STRICT	nr_seq_glicemia_w
	from 	atend_glicemia a
	where 	a.nr_prescricao = nr_prescricao_w
	and 	a.nr_seq_procedimento = nr_seq_procedimento_w;

end if;

return	nr_seq_glicemia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
