CREATE OR REPLACE FUNCTION cons_sobreposicao_hor_agepac (cd_agenda_p bigint, dt_agenda_p timestamp, nr_min_duracao_p bigint, cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE


ie_sobreposicao_w	smallint 	:= 0;
nr_seq_agenda_w	bigint	:= 0;
ds_sobreposicao_w	varchar(255);

c01 CURSOR FOR
SELECT	1 ie_sobreposicao,
	nr_sequencia nr_seq_agenda
from	agenda_paciente
where	ie_status_agenda not in ('C','L','B')
and	cd_pessoa_fisica = cd_pessoa_fisica_p
and	dt_agenda_p between hr_inicio and ((hr_inicio) + (nr_minuto_duracao/1440))
and	((hr_inicio) + (nr_minuto_duracao/1440)) > dt_agenda_p

union

select	2 ie_sobreposicao,
	nr_sequencia nr_seq_agenda
from	agenda_paciente
where	ie_status_agenda not in ('C','L','B')
and	cd_pessoa_fisica = cd_pessoa_fisica_p
and	trunc(dt_agenda,'dd') = trunc(dt_agenda_p,'dd')
and	dt_agenda_p + nr_min_duracao_p/1440 > hr_inicio
and	dt_agenda_p + nr_min_duracao_p/1440 < hr_inicio + nr_minuto_duracao/1440
order by
	ie_sobreposicao,
	nr_seq_agenda;


BEGIN
if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') and (nr_min_duracao_p IS NOT NULL AND nr_min_duracao_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	open c01;
	loop
	fetch c01 into	ie_sobreposicao_w,
				nr_seq_agenda_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		ie_sobreposicao_w	:= ie_sobreposicao_w;
		nr_seq_agenda_w	:= nr_seq_agenda_w;
		end;
	end loop;
	close c01;
end if;

return ie_sobreposicao_w;

end;
$body$
