CREATE OR REPLACE FUNCTION conta_tipo_atend_protocolo (nr_seq_protocolo_p bigint, cd_convenio_p bigint, ie_tipo_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

 
qt_atend_w	bigint;


BEGIN 
 
if (ie_tipo_atendimento_p IS NOT NULL AND ie_tipo_atendimento_p::text <> '') then 
	select	sum(CASE WHEN b.ie_tipo_atendimento=ie_tipo_atendimento_p THEN  1  ELSE 0 END ) 
	into STRICT	qt_atend_w 
	from	atendimento_paciente b, 
		conta_paciente a 
	where	a.nr_atendimento	= b.nr_atendimento 
	and	((coalesce(cd_convenio_p::text, '') = '') or (obter_convenio_atendimento(b.nr_atendimento)= cd_convenio_p)) 
	and	a.nr_seq_protocolo	= nr_seq_protocolo_p;
else 
	select	count(*) 
	into STRICT	qt_atend_w 
	from	atendimento_paciente b, 
		conta_paciente a 
	where	a.nr_atendimento	= b.nr_atendimento 
	and	((coalesce(cd_convenio_p::text, '') = '') or (obter_convenio_atendimento(b.nr_atendimento)= cd_convenio_p)) 
	and	a.nr_seq_protocolo	= nr_seq_protocolo_p;
end if;
 
return qt_atend_w;
 
end;
