CREATE OR REPLACE FUNCTION contem_prescr_plano_medic (nr_prescricoes_p text, nr_seq_proc_p bigint) RETURNS bigint AS $body$
DECLARE


nr_prescricoes_w                    varchar(2000);
ds_retorno_w                        smallint := 0;
is_care_plan                        smallint := 0;
nr_prescricao_w                     bigint;
pos_arr                             bigint;
loop_max                            bigint;
qtd_itens_w                         bigint;
nr_seq_pe_proc_w                    pe_prescr_proc.nr_sequencia%type;

BEGIN

    select (length(nr_prescricoes_p) - length(replace(nr_prescricoes_p,',','')) + 1) into STRICT loop_max;

    nr_prescricoes_w := replace(nr_prescricoes_p, ' ', '');

    begin
        for indice in 1..loop_max loop
            pos_arr := position(',' in nr_prescricoes_w) - 1;
            if (pos_arr = -1) then
                pos_arr := length(nr_prescricoes_w);
            end if;

            nr_prescricao_w := substr(nr_prescricoes_w,0,pos_arr);
            nr_prescricoes_w := regexp_replace(nr_prescricoes_w,nr_prescricao_w,'',1,1);--tirando prescricao encontrada da string
            nr_prescricoes_w := regexp_replace(nr_prescricoes_w,',','',1,1);--tirando primeira virgula encontrada da string
            select count(*)
            into STRICT is_care_plan 
            from PE_PRESCRICAO 
            where NR_SEQUENCIA = nr_prescricao_w and IE_TIPO = 'CP'
            and (nr_prescricao IS NOT NULL AND nr_prescricao::text <> '');

            if (is_care_plan = 1) then

                --busca a intervencao
                select max(nr_sequencia)
                into STRICT nr_seq_pe_proc_w
                from PE_PRESCR_PROC
                where nr_seq_prescr = nr_prescricao_w
                and nr_seq_proc = nr_seq_proc_p;--ADEP_HORARIOS_WEB.CD_ITEM
                -- verifica se existem itens associados
                select count(*)
                into STRICT qtd_itens_w
                from PRESCR_MATERIAL
                where nr_seq_pe_proc = nr_seq_pe_proc_w;

                if (qtd_itens_w > 0) then
                  ds_retorno_w := 1;
                  return ds_retorno_w;
                end if;

            end if;
        end loop;

    exception when others then
        ds_retorno_w := 0;
    end;
return ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
