CREATE OR REPLACE FUNCTION conteudo_campo_valido ( ds_Conteudo_Campo_p text, ds_Conteudo_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*	Opção
	SN - Sim Não
	X  - X quando Verdadeiro
*/
ds_result_sn_w			varchar(1);
ds_result_x_w			varchar(1);


BEGIN
ds_result_sn_w			:= 'N';
ds_result_x_w			:= '';
if (ds_Conteudo_Campo_p	= ds_Conteudo_p) then
	begin
	ds_result_sn_w		:= 'S';
	ds_result_x_w		:= 'X';
	end;
end if;


if (ie_opcao_p = 'X') then
	return ds_result_x_w;
else
	return ds_result_sn_w;
end if;
END;
$body$
