SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION controla_readonly_fanep ( nr_seq_pepo_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_read_only_w						varchar(1) := 'N';
ie_atualizar_w						varchar(1);
dt_liberacao_w  					timestamp;
dt_cancelamento_w					timestamp;
ie_permite_alterar_cancelada_w		varchar(1);
cd_perfil_w							integer;
nm_usuario_w						varchar(15);
cd_estabelecimento_w				integer;



BEGIN

cd_perfil_w				:= wheb_usuario_pck.get_cd_perfil;
nm_usuario_w			:= wheb_usuario_pck.get_nm_usuario;
cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;

ie_permite_alterar_cancelada_w := Obter_Param_Usuario(10026, 45, cd_perfil_w, nm_usuario_w, cd_estabelecimento_w, ie_permite_alterar_cancelada_w);

select  max(dt_liberacao),
		max(dt_cancelamento)
into STRICT	dt_liberacao_w,
		dt_cancelamento_w
from 	pepo_cirurgia
where 	nr_sequencia = nr_seq_pepo_p;

if (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then
	ie_read_only_w := 'S';
end if;


if (ie_read_only_w = 'N') then
	SELECT   coalesce(max(b.ie_atualizar),'S')
	INTO STRICT	 ie_atualizar_w
	FROM     fanep_item a,
			 perfil_item_fanep b
	WHERE    a.nr_sequencia	      =	b.nr_seq_item
	AND      b.cd_perfil	      = cd_perfil_w
	AND      a.nr_sequencia       = coalesce(nr_sequencia_p,0)
	ORDER BY  a.nr_sequencia;

	if (ie_atualizar_w = 'N') then
		ie_read_only_w := 'S';
	end if;
end if;

if (ie_read_only_w = 'N') and (ie_permite_alterar_cancelada_w = 'N') and (dt_cancelamento_w IS NOT NULL AND dt_cancelamento_w::text <> '') then
	ie_read_only_w := 'S';
end if;

return	ie_read_only_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
