CREATE OR REPLACE FUNCTION converter_hora_para_decimal (qt_hora_fase_p text) RETURNS bigint AS $body$
DECLARE


qt_hora_fase_w	varchar(200);
ds_hora_w		varchar(200);
ds_min_w		varchar(200);
qt_hora_w       double precision;
qt_min_w        double precision;
qt_retorno_w    double precision;


BEGIN

    qt_hora_fase_w := qt_hora_fase_p;
    ds_hora_w := substr(qt_hora_fase_w,1,position(':' in qt_hora_fase_w)-1);
    ds_min_w := substr(qt_hora_fase_w,position(':' in qt_hora_fase_w)+1,2);

    if substr(ds_hora_w,1,position('0' in ds_hora_w)) = '0' then
        ds_hora_w := substr(ds_hora_w,2);
    end if;

    qt_min_w := (ds_min_w)::numeric;
    qt_min_w := round((qt_min_w / 60)::numeric, 2);
    qt_hora_w := (ds_hora_w)::numeric;
    qt_retorno_w := qt_hora_w + qt_min_w;

    return round((qt_retorno_w)::numeric,2);

end;
