CREATE OR REPLACE FUNCTION converter_kcal_kjoules_md ( qt_valor_p bigint, ie_opcao text ) RETURNS bigint AS $body$
BEGIN
    IF ( coalesce(qt_valor_p, 0) > 0 ) THEN
        CASE upper(ie_opcao)
            WHEN 'KCAL' THEN
                RETURN round((qt_valor_p / 4.184)::numeric, 1);
            WHEN 'KJ' THEN
                RETURN round((qt_valor_p * 4.184)::numeric, 1);
            ELSE
                RETURN NULL;
        END CASE;
    END IF;

    RETURN NULL;
END;
$body$
