CREATE OR REPLACE FUNCTION converter_kcal_kjoules (valor_p bigint, ie_opcao text) RETURNS bigint AS $body$
BEGIN



if (coalesce(valor_p,0) > 0) then

 case upper(ie_opcao)

  when 'KCAL' then

   return round((valor_p / 4.184 )::numeric, 1);

  when 'KJ' then

   return round((valor_p * 4.184 )::numeric, 1);

  else return null;

 end case;

end if;



return null;



end;
