SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION converte_cor_delphi_rgb (corDelphi text) RETURNS varchar AS $body$
DECLARE


cor_w	varchar(50);


BEGIN
cor_w	:= upper(corDelphi);

if (substr(cor_w, 1, 2) = 'CL') then
	cor_w	:= substr(cor_w, 3, length(cor_w));
else
	cor_w	:= '#' || substr(cor_w, 8, 2) ||substr(cor_w, 6, 2) ||  substr(cor_w, 4, 2);
end if;


return	cor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
