SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION converte_cor_hex_delphi (CORHTML text) RETURNS varchar AS $body$
DECLARE


COR_W  varchar(50);


BEGIN
COR_W  := CORHTML;

IF (SUBSTR(CORHTML, 1, 1) = '#') THEN
COR_W  := UPPER(CORHTML);
COR_W  := '$00' || SUBSTR(COR_W, 6, 2) ||  SUBSTR(COR_W, 4, 2) ||  SUBSTR(COR_W, 2, 2);
END IF;

RETURN  COR_W;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
