SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION convert_html_to_str_nais (ds_campo_p text, ds_tabela_p text, ds_condicao_p text) RETURNS text AS $body$
DECLARE

						
x text;
in_html boolean  := FALSE;
s text;
ds_texto_w text;

BEGIN

ds_texto_w := convert_long_(ds_tabela_p,ds_campo_p,ds_condicao_p);
ds_texto_w := replace(ds_texto_w, '</p></body>', '--END--');
ds_texto_w := replace(ds_texto_w, '</p', '--BREAK--');
ds_texto_w := replace(ds_texto_w, '<br />', '--BREAK--');

if (ds_texto_w IS NOT NULL AND ds_texto_w::text <> '') and (position('<html' in ds_texto_w) > 0) then

	for i in 1 .. length( ds_texto_w ) loop
		s := substr( ds_texto_w, i, 1 );

		if in_html then
			if s = '>' then
				in_html := FALSE;
			end if;
		else
			if s = '<' then
				in_html := TRUE;
			end if;
		end if;

		if not in_html and s != '>' then
			x := x || s;
		end if;
	end loop;

	return x;
end if;	

return ds_texto_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
