CREATE OR REPLACE FUNCTION convert_integration_severity ( ds_severidade_p text ) RETURNS varchar AS $body$
DECLARE


	ds_severity_w varchar(25);


BEGIN
	if (ds_severidade_p IS NOT NULL AND ds_severidade_p::text <> '') then
		CASE ds_severidade_p
			WHEN 'LEVEL_1' THEN
				ds_severity_w := 'MINOR';
			WHEN 'LEVEL_2' THEN
				ds_severity_w := 'MODERATE';
			WHEN 'LEVEL_3' THEN
				ds_severity_w := 'MAJOR';
			WHEN 'LEVEL_4' THEN
				ds_severity_w := 'CONTRAINDICATED';
      ELSE
        ds_severity_w := null;
		END CASE;
	end if;

	return coalesce(ds_severity_w, ds_severidade_p);

end;
$body$
LANGUAGE PLPGSQL
