CREATE OR REPLACE FUNCTION convert_long_html_to_str (ds_campo_p text, ds_tabela_p text, ds_condicao_p text) RETURNS varchar AS $body$
DECLARE


x varchar(4000) := null;
in_html boolean  := FALSE;
s varchar(1);
ds_texto_w varchar(4000);

BEGIN

ds_texto_w := substr(convert_long_to_varchar2(ds_campo_p,ds_tabela_p,ds_condicao_p),1,4000);

if (ds_texto_w IS NOT NULL AND ds_texto_w::text <> '') and (position('<html' in ds_texto_w) > 0) then

	for i in 1 .. length( ds_texto_w ) loop
		s := substr( ds_texto_w, i, 1 );

		if in_html then
			if s = '>' then
				in_html := FALSE;
			end if;
		else
			if s = '<' then
				in_html := TRUE;
			end if;
		end if;

		if not in_html and s != '>' then
			x := substr(x || s,1,4000);
		end if;
	end loop;

	return x;
end if;

return ds_texto_w;

end;
$body$
LANGUAGE PLPGSQL
