CREATE OR REPLACE FUNCTION con_pr_conhecimento_consultor (cd_consultor_p bigint, ie_incluir_complementares_p text default 'N') RETURNS bigint AS $body$
DECLARE

			
ds_retorno_w	bigint;	


BEGIN

select	sum(pr_conhecimento)/coalesce(CASE WHEN count(*)=0 THEN 1  ELSE count(*) END ,1)
into STRICT	ds_retorno_w
from	com_cons_gest_con_mod a,
	com_cons_gest_con_fun b,
	modulo_implantacao c
where	a.nr_sequencia = b.nr_seq_conhecimento
and	a.nr_seq_mod_impl = c.nr_sequencia
and	a.nr_seq_consultor = cd_consultor_p	
and ((ie_incluir_complementares_p = 'S')	OR (ie_incluir_complementares_p = 'N'	and
	exists (SELECT	1
			from	proj_gest_conhecimento pgc
			where	pgc.cd_funcao = b.cd_funcao)));
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
