SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION con_pr_conhecimento_tasy (cd_consultor_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


soma_pr_fun_tasy_w	bigint;
soma_pr_fun_con_w	bigint;
vl_retorno_w		varchar(15);
qt_consultor_w		bigint;


BEGIN

select (count(distinct cd_funcao) * 100)
into STRICT	soma_pr_fun_tasy_w
from   	funcao
where  	ie_situacao = 'A';

select	count(a.cd_pessoa_fisica)
into STRICT	qt_consultor_w
from	pessoa_fisica b,
		pessoa_fisica_avaliacao c,
		com_canal_consultor a
where  	a.cd_pessoa_fisica = b.cd_pessoa_fisica
and		a.cd_pessoa_fisica = c.cd_pessoa_fisica
and		c.nr_sequencia = (
			SELECT   max(x.nr_sequencia)
			from     pessoa_fisica_avaliacao x
			where    x.cd_pessoa_fisica = a.cd_pessoa_fisica)
and		a.ie_situacao		= 'A'
and		a.ie_consultor_wheb	= 'S'
order by
		nm_pessoa_fisica;

if (ie_tipo_p = 'C') then
	select	sum(d.pr_conhecimento)
	into STRICT	soma_pr_fun_con_w
	from	com_cons_gest_con_mod a,
			modulo_implantacao b,
			com_canal_consultor c,
			com_cons_gest_con_fun d
	where	a.nr_seq_mod_impl = b.nr_sequencia
	and		c.nr_sequencia = a.nr_seq_consultor
	and		a.nr_sequencia = d.nr_seq_conhecimento
	and		c.cd_pessoa_fisica = cd_consultor_p;

select	substr(campo_mascara_virgula_casas(coalesce(((soma_pr_fun_con_w / soma_pr_fun_tasy_w) * 100),0),2),1,255)
into STRICT	vl_retorno_w
;

end if;

if (ie_tipo_p = 'G') then
	select	sum(d.pr_conhecimento)
	into STRICT	soma_pr_fun_con_w
	from	com_cons_gest_con_mod a,
			modulo_implantacao b,
			com_canal_consultor c,
			com_cons_gest_con_fun d
	where	a.nr_seq_mod_impl = b.nr_sequencia
	and		c.nr_sequencia = a.nr_seq_consultor
	and		a.nr_sequencia = d.nr_seq_conhecimento
	and		c.cd_pessoa_fisica in (
				SELECT	a.cd_pessoa_fisica
				from	pessoa_fisica b,
						pessoa_fisica_avaliacao c,
						com_canal_consultor a
				where  a.cd_pessoa_fisica      = b.cd_pessoa_fisica
				and	a.cd_pessoa_fisica = c.cd_pessoa_fisica
				and	c.nr_sequencia = (
						SELECT   max(x.nr_sequencia)
						from     pessoa_fisica_avaliacao x
						where    x.cd_pessoa_fisica = a.cd_pessoa_fisica)
				and	a.ie_situacao		= 'A'
				and	a.ie_consultor_wheb	= 'S');

select	substr(campo_mascara_virgula_casas(coalesce((soma_pr_fun_con_w / soma_pr_fun_tasy_w),0),2),1,255)
into STRICT	vl_retorno_w
;

end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION con_pr_conhecimento_tasy (cd_consultor_p bigint, ie_tipo_p text) FROM PUBLIC;

