CREATE OR REPLACE FUNCTION cpoe_atb_info_rel (ie_objetivo_p text, ie_indicacao_p text, ie_origem_infeccao_p text, cd_amostra_cih_p bigint, cd_topografia_cih_p bigint, cd_microorganismo_cih_p bigint, qt_dias_solicitado_p bigint, qt_dias_liberado_p bigint, dt_fim_cih_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);
ds_amostra_cultura_w		cih_amostra_cultura.ds_amostra_cultura%type;
ds_topografia_w				cih_topografia.ds_topografia%type;
ds_microorganismo_w			cih_microorganismo.ds_microorganismo%type;


BEGIN
if (ie_objetivo_p IS NOT NULL AND ie_objetivo_p::text <> '') then
	ds_retorno_w := substr(obter_desc_expressao(294569),1,70) || ': ' || substr(obter_valor_dominio(1280,ie_objetivo_p),1,255);
	if (ie_indicacao_p IS NOT NULL AND ie_indicacao_p::text <> '') then
		ds_retorno_w := ds_retorno_w || ' - ' || substr(obter_desc_expressao(291834),1,70) || ': ' || substr(obter_valor_dominio(1927,ie_indicacao_p),1,255);
	end if;
	if (ie_origem_infeccao_p IS NOT NULL AND ie_origem_infeccao_p::text <> '') then
		ds_retorno_w := ds_retorno_w || ' - ' || substr(obter_desc_expressao(294936),1,70) || ': ' || substr(obter_valor_dominio(1288,ie_origem_infeccao_p),1,255);
	end if;
	if (cd_amostra_cih_p IS NOT NULL AND cd_amostra_cih_p::text <> '') then
		select max(ds_amostra_cultura)
		into STRICT ds_amostra_cultura_w
		from cih_amostra_cultura
		where cd_amostra_cultura = cd_amostra_cih_p;

		ds_retorno_w := ds_retorno_w || ' - ' || substr(obter_desc_expressao(294302),1,70) || ': ' || ds_amostra_cultura_w;
	end if;
	if (cd_topografia_cih_p IS NOT NULL AND cd_topografia_cih_p::text <> '') then
		select max(ds_topografia)
		into STRICT ds_topografia_w
		from cih_topografia
		where cd_topografia = cd_topografia_cih_p;

		ds_retorno_w := ds_retorno_w || ' - ' || substr(obter_desc_expressao(300156),1,70) || ': ' || ds_topografia_w;
	end if;
	if (cd_microorganismo_cih_p IS NOT NULL AND cd_microorganismo_cih_p::text <> '') then
		select max(ds_microorganismo)
		into STRICT ds_microorganismo_w
		from cih_microorganismo
		where cd_microorganismo = cd_microorganismo_cih_p;

		ds_retorno_w := ds_retorno_w || ' - ' || substr(obter_desc_expressao(293300),1,70) || ': ' || ds_microorganismo_w;
	end if;
	if (qt_dias_solicitado_p IS NOT NULL AND qt_dias_solicitado_p::text <> '') then
		ds_retorno_w := ds_retorno_w || ' - ' || substr(obter_desc_expressao(291126),1,70) || ': ' || substr(qt_dias_solicitado_p,1,80);
	end if;
	if (qt_dias_liberado_p IS NOT NULL AND qt_dias_liberado_p::text <> '') then
		ds_retorno_w := ds_retorno_w || ' - ' || substr(obter_desc_expressao(291153),1,70) || ': ' || substr(qt_dias_liberado_p,1,80);
	end if;
	if (dt_fim_cih_p IS NOT NULL AND dt_fim_cih_p::text <> '') then
		ds_retorno_w := ds_retorno_w || ' - ' || substr(obter_desc_expressao(286879),1,70) || ': ' || substr(pkg_date_formaters.to_varchar(dt_fim_cih_p,'shortdate',1,wheb_usuario_pck.get_cd_estabelecimento,wheb_usuario_pck.get_nm_usuario),1,255);
	end if;
end if;

return ds_retorno_w;

end;
$body$
