SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cpoe_check_adm_route_rp ( cd_material_p bigint, nr_seq_rp_p bigint) RETURNS varchar AS $body$
DECLARE


ie_via_aplicacao_rp_w 			varchar(10);
ds_retorno_w 					varchar(4000) := null;
ie_via_aplicacao_material_w 	material.ie_via_aplicacao%type;
has_adm_route_w 				varchar(1) := 'N';

c_infusion_rules CURSOR FOR
SELECT  ie_via_aplicacao
from    mat_via_aplic
where   cd_material = cd_material_p;

BEGIN

select ie_via_aplicacao
into STRICT ie_via_aplicacao_material_w
from material
where cd_material = cd_material_p;

select  ie_via_aplicacao
into STRICT    ie_via_aplicacao_rp_w
from    cpoe_rp
where   nr_sequencia = nr_seq_rp_p;

for r_infusion_rule in c_infusion_rules loop
	if (ie_via_aplicacao_rp_w = r_infusion_rule.ie_via_aplicacao) then
		has_adm_route_w := 'Y';
        exit;
	end if;
end loop;

if (has_adm_route_w = 'N' AND ie_via_aplicacao_rp_w = ie_via_aplicacao_material_w) then
    has_adm_route_w := 'Y';
end if;

if (has_adm_route_w = 'N') then
    ds_retorno_w := obter_desc_expressao(1057052);
end if;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
