CREATE OR REPLACE FUNCTION cpoe_check_availability_mat (cd_estabelecimento_p bigint, cd_material_p bigint, cd_local_estoque_p bigint ) RETURNS varchar AS $body$
DECLARE


/*created for future handlings of the components of main material and stock location */
                          
ie_retorno_w		varchar(1) := 'S';
ie_consiste_estoque_w	material.ie_consiste_estoque%type;

BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '' AND cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then
	select	coalesce(ie_consiste_estoque,'S')
	into STRICT	ie_consiste_estoque_w
	from	material
	where	cd_material = cd_material_p;

	if (ie_consiste_estoque_w = 'S') then
		ie_retorno_w    := obter_se_item_disp(cd_estabelecimento_p,cd_material_p);
	end if;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
