CREATE OR REPLACE FUNCTION cpoe_check_max_days_incons (cd_material_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp) RETURNS varchar AS $body$
DECLARE


c01 CURSOR FOR
SELECT nr_max_days nr_max_days,
	coalesce(si_enable_order_submission, 'N') si_enable_order_submission
from cpoe_rule_max_days_check
where cd_material = cd_material_p
and nr_max_days < CPOE_CONT_PERIOD_CHK_PCK.get_distinct_days_count(dt_inicio_p, dt_fim_p);
/*
Using pkg_date_utils.Get_diffdate is not able to give expected output in scenarios like start_date= 18-FEB-2021 08:00:00 and end_date= 19-FEB-2021 18:00:00
The expected output in above example is 2 days(18 and 19). But pkg_date_utils.Get_diffdate gives 1 day only: pkg_date_utils.Get_diffdate(dt_inicio_p, dt_fim_p, 'DAY');
*/
--N = pass
--S = block
--J = justify
ie_return_w varchar(1) := 'N';

BEGIN
if (dt_inicio_p IS NOT NULL AND dt_inicio_p::text <> '' AND dt_fim_p IS NOT NULL AND dt_fim_p::text <> '') then
  for c01w in c01 loop
    ie_return_w := 'J';
    if (c01w.si_enable_order_submission = 'N') then
      ie_return_w := 'S';
      exit;
    end if;
  end loop;
end if;

return ie_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
