CREATE OR REPLACE FUNCTION cpoe_consiste_medic_espec_amb ( cd_material_p bigint, nr_seq_receita_p bigint, nm_usuario_p text, cd_perfil_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_receita_espec_w		valor_dominio.vl_dominio%type;
ie_tipo_receita_w			fa_receita_farmacia.ie_tipo_receita%type;
ie_receita_w				material.ie_receita%type;
ds_tipo_receita_w			varchar(255);

BEGIN

ie_tipo_receita_espec_w := obter_valor_param_usuario(10015, 114, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p);
if (ie_tipo_receita_espec_w IS NOT NULL AND ie_tipo_receita_espec_w::text <> '') and (coalesce(nr_seq_receita_p,0) > 0) then

	select	max(ie_tipo_receita)
	into STRICT	ie_tipo_receita_w
	from	fa_receita_farmacia
	where	nr_sequencia = nr_seq_receita_p;

	if (ie_tipo_receita_w IS NOT NULL AND ie_tipo_receita_w::text <> '') then

		ds_tipo_receita_w := obter_valor_dominio(5302, ie_tipo_receita_w);

		select 	coalesce(b.ie_receita,'N')
		into STRICT	ie_receita_w
		from	material b
		where 	b.cd_material = cd_material_p;

		if (ie_tipo_receita_espec_w = ie_tipo_receita_w) then
			if (ie_receita_w = 'S') then
				return null;
			elsif (ie_receita_w = 'N') then
				return wheb_mensagem_pck.get_texto(1004707,'DS_RECEITA=' || ds_tipo_receita_w);
			end if;
		else
			if (ie_receita_w = 'S') then
				return wheb_mensagem_pck.get_texto(1004707,'DS_RECEITA=' || ds_tipo_receita_w);
			elsif (ie_receita_w = 'N') then
				return null;
			end if;
		end if;
	else
		return null;
	end if;

end if;

return null;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
