SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cpoe_consistir_espec_medica ( cd_material_p bigint, cd_perfil_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

 
cd_grupo_material_w		bigint;
cd_subgrupo_material_w		bigint;
cd_classe_material_w		bigint;
cont_w				bigint;
cd_especialidade_w		bigint;
cd_especialidade_regra_w	bigint;
ie_consiste_w			varchar(1):='N';
nr_seq_regra_w			rep_medic_especialidade.nr_sequencia%type;
ie_permite_se_justificar_w 	rep_medic_especialidade.ie_permite_se_justificar%type;
ie_funcao_prescritor_w		rep_especialidade_medic.ie_funcao_prescritor%type;	
 
--Especialidades liberdas na regra 
c02 CURSOR FOR 
SELECT	cd_especialidade, 
	ie_funcao_prescritor 
from	rep_especialidade_medic 
where	nr_seq_regra = nr_seq_regra_w;


BEGIN 
 
select	Obter_estrutura_material(cd_material,'G'), 
	Obter_estrutura_material(cd_material,'S'), 
	Obter_estrutura_material(cd_material,'C') 
into STRICT	cd_grupo_material_w, 
	cd_subgrupo_material_w, 
	cd_classe_material_w 
from	material 
where	cd_material = cd_material_p;
 
select	count(*), 
	max(nr_sequencia), 
	max(coalesce(ie_permite_se_justificar,'N')) 
into STRICT	cont_w, 
	nr_seq_regra_w, 
	ie_permite_se_justificar_w 
from	rep_medic_especialidade 
where	((cd_material = cd_material_p) or (coalesce(cd_material::text, '') = '')) 
and	((cd_grupo_material = cd_grupo_material_w) or (coalesce(cd_grupo_material::text, '') = '')) 
and	((cd_subgrupo_material = cd_subgrupo_material_w) or (coalesce(cd_subgrupo_material::text, '') = '')) 
and	((cd_classe_material = cd_classe_material_w) or (coalesce(cd_classe_material::text, '') = '')) 
and	coalesce(ie_medico_enfermeira::text, '') = '';
 
if (cont_w > 0) then 
	ie_consiste_w := 'S';
 
	open C02;
	loop 
	fetch C02 into	 
		cd_especialidade_regra_w, 
		ie_funcao_prescritor_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
	 
		select	count(*) 
		into STRICT	cont_w 
		from	usuario a, 
			medico_especialidade b			 
		where	a.cd_pessoa_fisica = b.cd_pessoa_fisica 
		and	a.cd_pessoa_fisica = cd_pessoa_fisica_p 
		--and	a.ie_tipo_evolucao = ie_funcao_prescritor_w 
		and (b.cd_especialidade = cd_especialidade_regra_w or a.ie_tipo_evolucao = ie_funcao_prescritor_w);
		 
		if (cont_w	> 0) then 
			ie_consiste_w	:= 'N';				
		end if;
		 
	end loop;
	close C02;
	 
	if (ie_consiste_w = 'S') then 
		if (coalesce(ie_permite_se_justificar_w,'N') = 'N') then 
			return 'SJ';
		else 
			return 'S';
		end if;	
	end if;
end if;
 
return coalesce(ie_consiste_w,'N');
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
