SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cpoe_display_diet_alert ( nr_seq_proc_interno_p proc_interno.nr_sequencia%type, cd_pessoa_fisica_p atendimento_paciente.cd_pessoa_fisica%type, nr_atendimento_p cpoe_dieta.nr_atendimento%type, dt_inicio_p timestamp default null) RETURNS PROC_INTERNO.DS_ALERTA_VERIFICA_DIETA%TYPE AS $body$
DECLARE


ds_message_w	                proc_interno.ds_alerta_verifica_dieta%type;
has_diet_w                      bigint;
ie_tipo_atendimento_w           atendimento_paciente.ie_tipo_atendimento%type;


BEGIN

has_diet_w := 0;

select  COALESCE(MAX(b.ie_tipo_atendimento),0) into STRICT ie_tipo_atendimento_w
from  atendimento_paciente b
        WHERE b.cd_pessoa_fisica = cd_pessoa_fisica_p
        and  b.nr_atendimento = nr_atendimento_p;


select  ds_alerta_verifica_dieta into STRICT ds_message_w
        from proc_interno
where ie_verifica_dieta = 'S' 
and   nr_sequencia = nr_seq_proc_interno_p;

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '')
then
        select 
        cast(coalesce(MAX(has_diet),0) as integer) has_diet
        into STRICT 
        has_diet_w
        FROM (

        SELECT 
                count(*) as has_diet
        from
                cpoe_dieta a
        WHERE a.cd_pessoa_fisica = cd_pessoa_fisica_p
                AND cpoe_reg_valido_ativacao(CASE WHEN coalesce(a.dt_lib_suspensao::text, '') = '' THEN  a.dt_fim  ELSE coalesce(a.dt_suspensao, a.dt_fim) END , 
                                        a.dt_inicio,
                                        null,
                                        null,
                                        null, 
                                        a.ie_retrogrado, 
                                        a.dt_liberacao, 
                                        'N', 
                                        a.ie_futuro,
                                        a.dt_liberacao_fut) = 'S'
                and  (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
                and  a.nr_atendimento = nr_atendimento_p
				and dt_inicio_p between a.dt_inicio and coalesce(a.dt_fim, dt_inicio_p)
        ) a;
else
        select
        cast(coalesce(MAX(has_diet),0) as integer) has_diet
        into STRICT 
        has_diet_w
        FROM (

        SELECT 
                count(*) as has_diet
        from
                cpoe_dieta a
        WHERE a.cd_pessoa_fisica = cd_pessoa_fisica_p
                AND cpoe_reg_valido_ativacao(CASE WHEN coalesce(a.dt_lib_suspensao::text, '') = '' THEN  a.dt_fim  ELSE coalesce(a.dt_suspensao, a.dt_fim) END , 
                                        a.dt_inicio,
                                        null,
                                        null,
                                        null, 
                                        a.ie_retrogrado, 
                                        a.dt_liberacao, 
                                        'N', 
                                        a.ie_futuro,
                                        a.dt_liberacao_fut) = 'S'
                and  (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
				and dt_inicio_p between a.dt_inicio and coalesce(a.dt_fim, dt_inicio_p)
        ) a;

end if;

if coalesce(has_diet_w::text, '') = ''
then
        has_diet_w := 0;
end if;

if  (ds_message_w IS NOT NULL AND ds_message_w::text <> '') AND ( ie_tipo_atendimento_w in (1,7,8) or  coalesce(has_diet_w,0) > 0  )
then
        if (ie_tipo_atendimento_w = 8 and coalesce(has_diet_w,0) = 0) then
		return  null;
		
	else
		return  ds_message_w;
	end if;
else
        return null;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION cpoe_display_diet_alert ( nr_seq_proc_interno_p proc_interno.nr_sequencia%type, cd_pessoa_fisica_p atendimento_paciente.cd_pessoa_fisica%type, nr_atendimento_p cpoe_dieta.nr_atendimento%type, dt_inicio_p timestamp default null) FROM PUBLIC;

