CREATE OR REPLACE FUNCTION cpoe_display_order_reason (ie_motivo_prescricao_p rep_exibir_motivo_prescr.ie_motivo_prescricao%type, nm_usuario_p text, cd_perfil_p rep_exibir_motivo_prescr.cd_perfil%type) RETURNS varchar AS $body$
DECLARE

	

ie_funcao_prescritor_w     	usuario.ie_tipo_evolucao%type;
ie_exibir_itens_w		varchar(2):='S';

c01 CURSOR FOR
SELECT	ie_exibir_itens
from	rep_exibir_motivo_prescr
where	coalesce(ie_funcao_prescritor,ie_funcao_prescritor_w)	= ie_funcao_prescritor_w
and	coalesce(cd_perfil, cd_perfil_p)				= cd_perfil_p
and	coalesce(ie_motivo_prescricao,ie_motivo_prescricao_p)	= ie_motivo_prescricao_p
order by coalesce(cd_perfil,0),
	 coalesce(ie_motivo_prescricao,'0');


BEGIN

IF (coalesce(ie_motivo_prescricao_p::text, '') = '') THEN
	ie_exibir_itens_w:= 'S';
else	
	Select coalesce(max(ie_tipo_evolucao),0)
	into STRICT	ie_funcao_prescritor_w
	from	usuario
	where	nm_usuario = nm_usuario_p;
	
	open C01;
	loop
	fetch C01 into
		ie_exibir_itens_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		ie_exibir_itens_w	:= ie_exibir_itens_w;
	end loop;
	close C01;	

END IF;

return    ie_exibir_itens_w;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
