CREATE OR REPLACE FUNCTION cpoe_dose_limite_char (qt_dose_p bigint) RETURNS varchar AS $body$
DECLARE


qt_dose_w           varchar(50);

BEGIN

qt_dose_w := qt_dose_p;

if (substr(qt_dose_p,1,1) = ',') then
	qt_dose_w	:= '0' || qt_dose_p;
	qt_dose_w	:= replace(qt_dose_w,'.',',');
end if;

return qt_dose_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
