CREATE OR REPLACE FUNCTION cpoe_filtrar_somente_dil_red (ie_tipo_item_p text, cd_mat_princ_p bigint, cd_material_p bigint, cd_setor_atendimento_p text, ie_via_aplicacao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN

select	coalesce(max('N'),'X')
into STRICT	ds_retorno_w
from	material_diluicao
where	cd_material = cd_mat_princ_p
and 	cd_diluente = cd_material_p
and		cd_estabelecimento = obter_estabelecimento_ativo()
and		((ie_tipo_item_p = 'D' and ie_reconstituicao = 'N') or (ie_tipo_item_p = 'R' and ie_reconstituicao = 'R'));

if (ie_tipo_item_p in ('D','R') and ds_retorno_w = 'N') then
	
	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	material_diluicao
	where	cd_material = cd_mat_princ_p
	and 	cd_diluente = cd_material_p
	and		cd_estabelecimento = obter_estabelecimento_ativo()
	and		((ie_tipo_item_p = 'D' and ie_reconstituicao = 'N') or (ie_tipo_item_p = 'R' and ie_reconstituicao = 'R'))
	and		((coalesce(cd_setor_atendimento,cd_setor_atendimento_p) = cd_setor_atendimento_p) or (cd_setor_atendimento_p = 0))
	and		((ie_via_aplicacao = ie_via_aplicacao_p) or (coalesce(ie_via_aplicacao::text, '') = '') or (coalesce(ie_via_aplicacao_p::text, '') = ''));
end if;

return	ds_retorno_w;

end;
$body$
