CREATE OR REPLACE FUNCTION cpoe_formata_ds_horarios (ds_horarios_p text) RETURNS varchar AS $body$
DECLARE

ds_horarios_aux_x	varchar(4000);
ds_horarios_aux2_x	varchar(4000);
ds_horarios_aux3_x	varchar(4000) := -1;
ds_retorno_w		varchar(4000);

BEGIN
ds_horarios_aux2_x := ds_horarios_p;

for i in 1..length(ds_horarios_aux2_x) loop
	begin
	if (position(':' in ds_horarios_aux2_x) > 0) then
		ds_horarios_aux_x := ltrim(substr(ds_horarios_aux2_x,1,position(':' in ds_horarios_aux2_x)-1));
		ds_horarios_aux2_x := substr(ds_horarios_aux2_x,position(' ' in ds_horarios_aux2_x) + 1,length(ds_horarios_aux2_x));

		if (ds_horarios_aux3_x <> ds_horarios_aux_x) then
			ds_retorno_w := ds_retorno_w ||' '||ds_horarios_aux_x;
			ds_horarios_aux3_x := ds_horarios_aux_x;
		end if;
	else
		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w := ds_horarios_p;
		end if;
	end if;
	end;
end loop;

return trim(both ds_retorno_w);
end;
$body$
