CREATE OR REPLACE FUNCTION cpoe_get_calc_end_date (dt_start_p timestamp, qt_days_p bigint, si_dif_interval_p text, si_sunday_p text default null, si_monday_p text default null, si_tuesday_p text default null, si_wednesday_p text default null, si_thursday_p text default null, si_friday_p text default null, si_saturday_p text default null) RETURNS timestamp AS $body$
DECLARE

								
dt_end_w	timestamp	:= null;
dt_reference_w	timestamp	:= null;
qt_days_w	bigint;


BEGIN

if (dt_start_p IS NOT NULL AND dt_start_p::text <> '') and (qt_days_p > 0) then
	if (si_dif_interval_p = 'Y') then
		dt_reference_w	:= dt_start_p;
		
		qt_days_w	:= 0;
		while(qt_days_w < qt_days_p) and (dt_reference_w <= dt_reference_w + 100) loop
			
			if	((si_sunday_p = 'Y') and (to_char(dt_reference_w,'d') = 1)) or
				((si_monday_p = 'Y') and (to_char(dt_reference_w,'d') = 2)) or
				((si_tuesday_p = 'Y') and (to_char(dt_reference_w,'d') = 3)) or
				((si_wednesday_p = 'Y') and (to_char(dt_reference_w,'d') = 4)) or
				((si_thursday_p = 'Y') and (to_char(dt_reference_w,'d') = 5)) or
				((si_friday_p = 'Y') and (to_char(dt_reference_w,'d') = 6)) or
				((si_saturday_p = 'Y') and (to_char(dt_reference_w,'d') = 7)) then
				qt_days_w	:= qt_days_w + 1;
			end if;
			
			dt_reference_w	:= dt_reference_w + 1;
		end loop;
		
		if (qt_days_w > 0) then
			dt_end_w	:= dt_reference_w  - 1/24/60;
		else
			dt_end_w	:= null;
		end if;
	else
		dt_end_w	:= dt_start_p + qt_days_p - 1/24/60;
	end if;
end if;

return	dt_end_w;

end;
$body$
